package Ngay1204;public class Brand {    private int id;   private String name;    private static int idU=1;    public Brand() {        this.id=idU++;    }    public Brand( String name) {        this.id=idU++;        this.name = name;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    @Override    public String toString() {        return "Brand{" +                "id=" + id +                ", name='" + name + '\'' +                '}';    }    public void setName(String name) {        this.name = name;    }}